import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const RSVPForm = ({ onSubmit, eventId }) => {
  const validationSchema = Yup.object({
    status: Yup.string()
      .oneOf(['confirmed', 'pending', 'canceled'], 'Invalid status')
      .required('Status is required'),
    guests: Yup.number()
      .min(0, 'Number of guests cannot be negative')
      .integer('Must be a whole number')
      .required('Please specify number of guests'),
  });

  return (
    <Formik
      initialValues={{ status: 'confirmed', guests: 1 }}
      validationSchema={validationSchema}
      onSubmit={(values) => onSubmit({ ...values, event_id: eventId })}
    >
      {({ isSubmitting }) => (
        <Form className="rsvp-form">
          <div className="form-group">
            <label htmlFor="status">RSVP Status</label>
            <Field as="select" id="status" name="status">
              <option value="confirmed">Confirmed</option>
              <option value="pending">Pending</option>
              <option value="canceled">Canceled</option>
            </Field>
            <ErrorMessage name="status" component="div" className="error" />
          </div>

          <div className="form-group">
            <label htmlFor="guests">Number of Guests</label>
            <Field type="number" id="guests" name="guests" min="1" />
            <ErrorMessage name="guests" component="div" className="error" />
          </div>

          <button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Submitting...' : 'Submit RSVP'}
          </button>
        </Form>
      )}
    </Formik>
  );
};

export default RSVPForm;
